import org.apache.commons.lang.StringUtils;

StringBuilder nuxeoRequest = new StringBuilder();
nuxeoRequest.append("ecm:path STARTSWITH '").append(basePath).append("' ");

// Keywords
String keywords = StringUtils.join(params.get("keywords"), " ");
if (StringUtils.isNotEmpty(keywords)) {
nuxeoRequest.append("AND (ecm:fulltext = '").append(keywords).append("' ");
nuxeoRequest.append("OR dc:title ILIKE '").append(keywords).append("%' ");
for(String keyword:params.get("keywords")){
nuxeoRequest.append("OR idxcl:keywords ILIKE '").append(keywords).append("%' ");
}
nuxeoRequest.append(") ");
}


// Document types
List documentTypes = params.get("documentTypes");
if ((documentTypes != null) && !documentTypes.isEmpty()) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String documentType : documentTypes) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:documentTypes = '").append(documentType).append("' ");
}
nuxeoRequest.append(") ");
}

// Levels
List levels = params.get("levels");
if ((levels != null) && !levels.isEmpty()) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String level : levels) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:levelsTree STARTSWITH '").append(level).append("' ");
}
nuxeoRequest.append(") ");
}

// Subjects
List subjects = params.get("subjects");
if ((subjects != null) && !subjects.isEmpty()) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String subject : subjects) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:subjectsTree STARTSWITH '").append(subject).append("' ");
}
nuxeoRequest.append(") ");
}

// Format
List formats= params.get("formats");
if ((formats!= null) && !formats.isEmpty()) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String format: formats) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:format = '").append(format).append("' ");
}
nuxeoRequest.append(") ");
}

List sizes = params.get("size");
List sizeRanges = params.get("size-range");

if ((sizes!= null) && !sizes.isEmpty() && (sizeRanges!= null) && !sizeRanges.isEmpty()) {
nuxeoRequest.append("AND file:content/length ").append("LESS".equals(sizeRanges.get(0)) ? "<" : ">").append(" ").append(sizes.get(0)).append(" ");
}


// Authors
List authors= params.get("authors");
if ((authors!= null) && !authors.isEmpty()) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String author: authors) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("dc:lastContributor ='").append(author).append("' ");
}
nuxeoRequest.append(") ");
}

// Date
List dates = params.get("date");
if ((dates != null) && !dates .isEmpty()) {
nuxeoRequest.append("AND dc:modified >= DATE '").append(dates.get(0)).append("' ");
}


return nuxeoRequest.toString();

import org.apache.commons.lang.StringUtils;
import org.apache.commons.collections.CollectionUtils;

// Keywords
String keywords = StringUtils.join(params.get("keywords"), " ");
// Document types
List documentTypes = params.get("documentTypes");
// Levels
List levels = params.get("levels");
// Subjects
List subjects = params.get("subjects");
// Size
List sizes = params.get("size");
List sizeRanges = params.get("size-range");
// Date
List dates = params.get("date");
// Format
List formats = params.get("formats");
// Shared
List shareds = params.get("shareds");

StringBuilder nuxeoRequest = new StringBuilder();
if (StringUtils.isEmpty(keywords) && CollectionUtils.isEmpty(documentTypes) && CollectionUtils.isEmpty(levels) && CollectionUtils.isEmpty(subjects) && CollectionUtils.isEmpty(sizes) && CollectionUtils.isEmpty(sizeRanges) && CollectionUtils.isEmpty(dates) && CollectionUtils.isEmpty(formats) && CollectionUtils.isEmpty(shareds )) {
nuxeoRequest.append("ecm:parentId = '").append(navigationPubInfos.getLiveId()).append("' ");
} else {
nuxeoRequest.append("ecm:path STARTSWITH '").append(navigationPath).append("' ");
}

if (StringUtils.isNotEmpty(keywords)) {
nuxeoRequest.append("AND (ecm:fulltext = '").append(keywords).append("' ");
nuxeoRequest.append("OR dc:title ILIKE '").append(keywords).append("%' ");
for(String keyword:params.get("keywords")){
nuxeoRequest.append("OR idxcl:keywords ILIKE '").append(keywords).append("%' ");
}
nuxeoRequest.append(") ");
}


if (CollectionUtils.isNotEmpty(documentTypes)) {
nuxeoRequest.append("AND idxcl:documentTypes IN ('").append(StringUtils.join(documentTypes, "', '")).append("') ");
}

if (CollectionUtils.isNotEmpty(levels)) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String level : levels) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:levelsTree STARTSWITH '").append(level).append("' ");
}
nuxeoRequest.append(") ");
}

if (CollectionUtils.isNotEmpty(subjects)) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String subject : subjects) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:subjectsTree STARTSWITH '").append(subject).append("' ");
}
nuxeoRequest.append(") ");
}

if (CollectionUtils.isNotEmpty(sizes) && CollectionUtils.isNotEmpty(sizeRanges)) {
nuxeoRequest.append("AND file:content/length ").append("LESS".equals(sizeRanges.get(0)) ? "<" : ">").append(" ").append(sizes.get(0)).append(" ");
}

if (CollectionUtils.isNotEmpty(dates)) {
nuxeoRequest.append("AND dc:modified >= DATE '").append(dates.get(0)).append("' ");
}

if (CollectionUtils.isNotEmpty(formats)) {
boolean first = true;
nuxeoRequest.append("AND (");
for (String format: formats) {
first ? first = false : nuxeoRequest.append("OR ");
nuxeoRequest.append("idxcl:format = '").append(format).append("' ");
}
nuxeoRequest.append(") ");
}

if (CollectionUtils.isNotEmpty(shareds)) {
boolean first = true;
StringBuilder nuxeoShare = new StringBuilder();
for (String shared: shareds) {
first ? first = false : nuxeoShare.append("OR ");
if ("MUTUALIZED".equals(shared)) {
nuxeoShare.append("mtz:enable = 1 ");
} else if ("PRONOTE".equals(shared)) {
nuxeoShare.append("rshr:targets/*/pubId IS NOT NULL ");
} else if ("NOT_SHARED".equals(shared)) {
nuxeoShare.append("mtz:enable <> 1 AND rshr:targets/*/pubId IS NULL AND ecm:mixinType <> 'Folderish' ");
} else if ("SHARED".equals(shared)) {
nuxeoShare.append("rshr:enabledLink = 1 ");
}
}
if( nuxeoShare.length()>0)
nuxeoRequest.append("AND ("+nuxeoShare.toString()+") ");
}




return nuxeoRequest.toString();

